import React, { Fragment } from 'react';
import Message from '../feed/feedPost/feedPost.component';
import Button from '../button/button.component';
import ErrorHandler from '../ErrorHandler/ErrorHandler.component';
import Paginator from '../Paginator/Paginator.component';
import Loader from '../Loader/Loader';


class TableWrapper extends React.Component {
    constructor(props) {
        super(props) //since we are extending class Table so we have to use super in order to override Component class constructor
        this.state = { //state is by default an object
            messages: [
                { id: 1, name: 'Wasif', age: 21, messageDate: '2467897', email: 'wasif@email.com' },
                { id: 2, name: 'Ali', age: 19, messageDate: '2467897', email: 'ali@email.com' },
                { id: 3, name: 'Saad', age: 16, messageDate: '2467897', email: 'saad@email.com' },
                { id: 4, name: 'Asad', age: 25, messageDate: '2467897', email: 'asad@email.com' }
            ],
            error: false
        }
    };

    
    componentDidMount() {
        fetch('http://localhost:3001/feed/messages')
          .then(res => {
            if (res.status !== 200) {
              throw new Error('Failed to fetch user status.');
            }
            return res.json();
          })
          .then(resData => {
            this.setState({ status: resData.status });
          })
          .catch(this.catchError);
    
        this.loadMessages();
      }
    
      loadMessages = direction => {
        if (direction) {
          this.setState({ messagesLoading: true, messages: [] });
        }
        let page = this.state.messagePage;
        if (direction === 'next') {
          page++;
          this.setState({ messagePage: page });
        }
        if (direction === 'previous') {
          page--;
          this.setState({ messagePage: page });
        }
        fetch('http://localhost:3001/feed/messages')
          .then(res => {
            if (res.status !== 200) {
              throw new Error('Failed to fetch messages.');
            }
            return res.json();
          })
          .then(resData => {
            this.setState({
                messages: resData.messages,
                totalMessages: resData.totalItems,
                messageLoading: false
            });
          })
          .catch(this.catchError);
      };

    errorHandler = () => {
        this.setState({ error: null });
    };

    render() {

        return (
            <Fragment>
                <ErrorHandler error={this.state.error} onHandle={this.errorHandler} />
                <section className="feed__status">
                    <form onSubmit={this.statusUpdateHandler}>
                        <Button mode="flat" type="submit" buttonContent={'Update'} />      
                    </form>
                </section>
                <section className="feed__control">
                    <Button mode="raised" design="accent" onClick={this.newPostHandler} buttonContent={'New Post'} />     
                </section>
                <section className="feed">
                    {this.state.postsLoading && (
                        <div style={{ textAlign: 'center', marginTop: '2rem' }}>
                            <Loader />
                        </div>
                    )}
                    {this.state.posts.length <= 0 && !this.state.postsLoading ? (
                        <p style={{ textAlign: 'center' }}>No posts found.</p>
                    ) : null}
                    {!this.state.postsLoading && (
                        <Paginator
                            onPrevious={this.loadPosts.bind(this, 'previous')}
                            onNext={this.loadPosts.bind(this, 'next')}
                            lastPage={Math.ceil(this.state.totalPosts / 2)}
                            currentPage={this.state.postPage}
                        >
                            {this.state.posts.map(post => (
                                <Message
                                    key={post._id}
                                    id={post._id}
                                    author={post.creator.name}
                                    date={new Date(post.createdAt).toLocaleDateString('en-US')}
                                    title={post.title}
                                    image={post.imageUrl}
                                    content={post.content}
                                    onStartEdit={this.startEditPostHandler.bind(this, post._id)}
                                    onDelete={this.deletePostHandler.bind(this, post._id)}
                                />
                            ))}
                        </Paginator>
                    )}
                </section>
            </Fragment>
        );
    }
}

export default TableWrapper;
