import React from 'react';
import './button.styles.css';

const Button = ({ buttonType, buttonContent }) => {
    return (
        <button
            className={[
                'button',
                `button--${props.design}`,
                `button--${props.mode}`
            ].join(' ')}
            onClick={props.onClick}
            disabled={props.disabled || props.loading}
            type={props.type}
        >
            {props.loading ? 'Loading...' : props.children}
        </button>
    )

}

export default Button;
